            URL

        .PARAMETER hreflang
        Specifies the language of the linked document
        Accepted Values:
            language_code

        .PARAMETER media
        Specifies what media/device the linked document is optimized for
        Accepted Values:
            media_query

        .PARAMETER ping
        Specifies a space-separated list of URLs to which, when the link is followed, post requests with the body ping will be sent by the browser (in the background). Typically used for tracking.
        Accepted Values:
            list_of_URLs

        .PARAMETER referrerpolicy
        Specifies which referrer to send
        Accepted Values:
            no-referrer
			no-referrer-when-downgrade
			origin
			origin-when-cross-origin
			unsafe-url

        .PARAMETER rel
        Specifies the relationship between the current document and the linked document
        Accepted Values:
            alternate
			author
			bookmark
			external
			help
			license
			next
			nofollow
			noreferrer
			noopener
			prev
			search
			tag

        .PARAMETER target
        Specifies where to open the linked document
        Accepted Values:
            _blank
			_parent
			_self
			_top
			framename

        .PARAMETER type
        Specifies the media type of the linked document
        Accepted Values:
            media_type

        .PARAMETER accesskey
        Specifies a shortcut key to activate/focus an element

        .PARAMETER class
        Specifies one or more classnames for an element (refers to a class in a style sheet)

        .PARAMETER contenteditable
        Specifies whether the content of an element is editable or not

        .PARAMETER dir
        Specifies the text direction for the content in an element

        .PARAMETER draggable
        Specifies whether an element is draggable or not

        .PARAMETER dropzone
        Specifies whether the dragged data is copied, moved, or linked, when dropped

        .PARAMETER hidden
        Specifies that an element is not yet, or is no longer, relevant

        .PARAMETER id
        Specifies a unique id for an element

        .PARAMETER lang
        Specifies the language of the element's content

        .PARAMETER spellcheck
        Specifies whether the element is to have its spelling and grammar checked or not

        .PARAMETER style
        Specifies an inline CSS style for an element

        .PARAMETER tabindex
        Specifies the tabbing order of an element

        .PARAMETER title
        Specifies extra information about an element

        .PARAMETER translate
        Specifies whether the content of an element should be translated or not

        .PARAMETER onafterprint
        Script to be run after the document is printed
        Accepted Values:
            script

        .PARAMETER onbeforeprint
        Script to be run before the document is printed
        Accepted Values:
            script

        .PARAMETER onbeforeunload
        Script to be run when the document is about to be unloaded
        Accepted Values:
            script

        .PARAMETER onerror
        Script to be run when an error occurs
        Accepted Values:
            script

        .PARAMETER onhashchange
        Script to be run when there has been changes to the anchor part of the a URL
        Accepted Values:
            script

        .PARAMETER onload
        Fires after the page is finished loading
        Accepted Values:
            script

        .PARAMETER onmessage
        Script to be run when the message is triggered
        Accepted Values:
            script

        .PARAMETER onoffline
        Script to be run when the browser starts to work offline
        Accepted Values:
            script

        .PARAMETER ononline
        Script to be run when the browser starts to work online
        Accepted Values:
            script

        .PARAMETER onpagehide
        Script to be run when a user navigates away from a page
        Accepted Values:
            script

        .PARAMETER onpageshow
        Script to be run when a user navigates to a page
        Accepted Values:
            script

        .PARAMETER onpopstate
        Script to be run when the window's history changes
        Accepted Values:
            script

        .PARAMETER onresize
        Fires when the browser window is resized
        Accepted Values:
            script

        .PARAMETER onstorage
        Script to be run when a Web Storage area is updated
        Accepted Values:
            script

        .PARAMETER onunload
        Fires once a page has unloaded (or the browser window has been closed)
        Accepted Values:
            script

        .PARAMETER onblur
        Fires the moment that the element loses focus
        Accepted Values:
            script

        .PARAMETER onchange
        Fires the moment when the value of the element is changed
        Accepted Values:
            script

        .PARAMETER oncontextmenu
        Script to be run when a context menu is triggered
        Accepted Values:
            script

        .PARAMETER onfocus
        Fires the moment when the element gets focus
        Accepted Values:
            script

        .PARAMETER oninput
        Script to be run when an element gets user input
        Accepted Values:
            script

        .PARAMETER oninvalid
        Script to be run when an element is invalid
        Accepted Values:
            script

        .PARAMETER onreset
        Fires when the Reset button in a form is clicked
        Accepted Values:
            script

        .PARAMETER onsearch
        Fires when the user writes something in a search field (for <input="search">)
        Accepted Values:
            script

        .PARAMETER onselect
        Fires after some text has been selected in an element
        Accepted Values:
            script

        .PARAMETER onsubmit
        Fires when a form is submitted
        Accepted Values:
            script

        .PARAMETER onkeydown
        Fires when a user is pressing a key
        Accepted Values:
            script

        .PARAMETER onkeypress
        Fires when a user presses a key
        Accepted Values:
            script

        .PARAMETER onkeyup
        Fires when a user releases a key
        Accepted Values:
            script

        .PARAMETER onclick
        Fires on a mouse click on the element
        Accepted Values:
            script

        .PARAMETER ondblclick
        Fires on a mouse double-click on the element
        Accepted Values:
            script

        .PARAMETER onmousedown
        Fires when a mouse button is pressed down on an element
        Accepted Values:
            script

        .PARAMETER onmousemove
        Fires when the mouse pointer is moving while it is over an element
        Accepted Values:
            script

        .PARAMETER onmouseout
        Fires when the mouse pointer moves out of an element
        Accepted Values:
            script

        .PARAMETER onmouseover
        Fires when the mouse pointer moves over an element
        Accepted Values:
            script

        .PARAMETER onmouseup
        Fires when a mouse button is released over an element
        Accepted Values:
            script

        .PARAMETER onmousewheel
        Deprecated. Use the onwheel attribute instead
        Accepted Values:
            script

        .PARAMETER onwheel
        Fires when the mouse wheel rolls up or down over an element
        Accepted Values:
            script

        .PARAMETER ondrag
        Script to be run when an element is dragged
        Accepted Values:
            script

        .PARAMETER ondragend
        Script to be run at the end of a drag operation
        Accepted Values:
            script

        .PARAMETER ondragenter
        Script to be run when an element has been dragged to a valid drop target
        Accepted Values:
            script

        .PARAMETER ondragleave
        Script to be run when an element leaves a valid drop target
        Accepted Values:
            script

        .PARAMETER ondragover
        Script to be run when an element is being dragged over a valid drop target
        Accepted Values:
            script

        .PARAMETER ondragstart
        Script to be run at the start of a drag operation
        Accepted Values:
            script

        .PARAMETER ondrop
        Script to be run when dragged element is being dropped
        Accepted Values:
            script

        .PARAMETER onscroll
        Script to be run when an element's scrollbar is being scrolled
        Accepted Values:
            script

        .PARAMETER oncopy
        Fires when the user copies the content of an element
        Accepted Values:
            script

        .PARAMETER oncut
        Fires when the user cuts the content of an element
        Accepted Values:
            script

        .PARAMETER onpaste
        Fires when the user pastes some content in an element
        Accepted Values:
            script

        .PARAMETER onabort
        Script to be run on abort
        Accepted Values:
            script

        .PARAMETER oncanplay
        Script to be run when a file is ready to start playing (when it has buffered enough to begin)
        Accepted Values:
            script

        .PARAMETER oncanplaythrough
        Script to be run when a file can be played all the way to the end without pausing for buffering
        Accepted Values:
            script

        .PARAMETER oncuechange
        Script to be run when the cue changes in a <track> element
        Accepted Values:
            script

        .PARAMETER ondurationchange
        Script to be run when the length of the media changes
        Accepted Values:
            script

        .PARAMETER onemptied
        Script to be run when something bad happens and the file is suddenly unavailable (like unexpectedly disconnects)
        Accepted Values:
            script

        .PARAMETER onended
        Script to be run when the media has reach the end (a useful event for messages like "thanks for listening")
        Accepted Values:
            script

        .PARAMETER onloadeddata
        Script to be run when media data is loaded
        Accepted Values:
            script

        .PARAMETER onloadedmetadata
        Script to be run when meta data (like dimensions and duration) are loaded
        Accepted Values:
            script

        .PARAMETER onloadstart
        Script to be run just as the file begins to load before anything is actually loaded
        Accepted Values:
            script

        .PARAMETER onpause
        Script to be run when the media is paused either by the user or programmatically
        Accepted Values:
            script

        .PARAMETER onplay
        Script to be run when the media is ready to start playing
        Accepted Values:
            script

        .PARAMETER onplaying
        Script to be run when the media actually has started playing
        Accepted Values:
            script

        .PARAMETER onprogress
        Script to be run when the browser is in the process of getting the media data
        Accepted Values:
            script

        .PARAMETER onratechange
        Script to be run each time the playback rate changes (like when a user switches to a slow motion or fast forward mode)
        Accepted Values:
            script

        .PARAMETER onseeked
        Script to be run when the seeking attribute is set to false indicating that seeking has ended
        Accepted Values:
            script

        .PARAMETER onseeking
        Script to be run when the seeking attribute is set to true indicating that seeking is active
        Accepted Values:
            script

        .PARAMETER onstalled
        Script to be run when the browser is unable to fetch the media data for whatever reason
        Accepted Values:
            script

        .PARAMETER onsuspend
        Script to be run when fetching the media data is stopped before it is completely loaded for whatever reason
        Accepted Values:
            script

        .PARAMETER ontimeupdate
        Script to be run when the playing position has changed (like when the user fast forwards to a different point in the media)
        Accepted Values:
            script

        .PARAMETER onvolumechange
        Script to be run each time the volume is changed which (includes setting the volume to "mute")
        Accepted Values:
            script

        .PARAMETER onwaiting
        Script to be run when the media has paused but is expected to resume (like when the media pauses to buffer more data)
        Accepted Values:
            script

        .PARAMETER ontoggle
        Fires when the user opens or closes the <details> element
        Accepted Values:
            script


        .PARAMETER OtherAttributes
        Allows you to provide any other attributes to the HTML tag
    #>
    [CmdletBinding()]
    Param(

        $download,

        $href,

        $hreflang,

        $media,

        $ping,

        $referrerpolicy,

        $rel,

        $target,

        $type,

        $accesskey,

        $class,

        $contenteditable,

        $dir,

        $draggable,

        $dropzone,

        $hidden,

        $id,

        $lang,

        $spellcheck,

        $style,

        $tabindex,

        $title,

        $translate,

        $onafterprint,

        $onbeforeprint,

        $onbeforeunload,

        $onerror,

        $onhashchange,

        $onload,

        $onmessage,

        $onoffline,

        $ononline,

        $onpagehide,

        $onpageshow,

        $onpopstate,

        $onresize,

        $onstorage,

        $onunload,

        $onblur,

        $onchange,

        $oncontextmenu,

        $onfocus,

        $oninput,

        $oninvalid,

        $onreset,

        $onsearch,

        $onselect,

        $onsubmit,

        $onkeydown,

        $onkeypress,

        $onkeyup,

        $onclick,

        $ondblclick,

        $onmousedown,

        $onmousemove,

        $onmouseout,

        $onmouseover,

        $onmouseup,

        $onmousewheel,

        $onwheel,

        $ondrag,

        $ondragend,

        $ondragenter,

        $ondragleave,

        $ondragover,

        $ondragstart,

        $ondrop,

        $onscroll,

        $oncopy,

        $oncut,

        $onpaste,

        $onabort,

        $oncanplay,

        $oncanplaythrough,

        $oncuechange,

        $ondurationchange,

        $onemptied,

        $onended,

        $onloadeddata,

        $onloadedmetadata,

        $onloadstart,

        $onpause,

        $onplay,

        $onplaying,

        $onprogress,

        $onratechange,

        $onseeked,

        $onseeking,

        $onstalled,

        $onsuspend,

        $ontimeupdate,

        $onvolumechange,

        $onwaiting,

        $ontoggle,

        $Content,

        [Collections.Hashtable]$OtherAttributes
    )

    $Output = '<A'

    foreach ($key in ($MyInvocation.BoundParameters.keys | Where-Object -FilterScript {$_ -ne 'content'})) {
        if ($key -eq 'OtherAttributes') {
            $OtherAttributes = (Get-Variable -Name $key).Value
            $OtherAttributes.Keys | ForEach-Object -Process {
                $Output += " $($_)=`"$($OtherAttributes[$_])`""
            }
        } else {
            $value = (Get-Variable -Name $key).Value
            if ($key -match '_') {$key -replace '_','-'}
            $Output += " $key=`"$value`""
        }
    }

    if ($Content -is [Management.Automation.ScriptBlock]) {
        $Out = $Content.Invoke()
    } else {
        $Out = $Content
    }

    $Output += ">$Out</A>"

    Write-Output -InputObject $Output
}
